function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((c=>{c.addEventListener("click",(()=>{const e=c.parentNode;e.classList.contains("accordion__item-active")?e.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((c=>c.classList.remove("accordion__item-active"))),e.classList.add("accordion__item-active"))}))}))}accordion();