function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const o=e.parentNode;o.classList.contains("accordion__item-active")?o.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),o.classList.add("accordion__item-active"))}))}))}accordion();const smoothLinks=document.querySelectorAll('a[href^="#"]');for(let e of smoothLinks)e.addEventListener("click",(function(o){o.preventDefault();const t=e.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth",block:"start"})}));